}
.bg-obsidian {
  background:
    radial-gradient(1200px 600px at 12% 0%, rgba(84, 110, 122, 0.22), transparent 60%),
    radial-gradient(1000px 500px at 85% 0%, rgba(69, 90, 100, 0.16), transparent 60%),
    linear-gradient(180deg, #F1F4F6 0%, #EFF2F4 40%, #FAFAFA 100%);
}
.bg-teal {
