}
.bg-hematite {
  background:
    radial-gradient(1200px 600px at 12% 0%, rgba(229, 233, 238, 0.18), transparent 60%),
    radial-gradient(1000px 500px at 88% 0%, rgba(184, 192, 200, 0.14), transparent 60%),
    linear-gradient(180deg, #F5F7F9 0%, #F2F4F6 40%, #FAFAFA 100%);
}
.bg-contact {
  background:
    radial-gradient(1200px 600px at 12% 0%, rgba(156, 163, 175, 0.35), transparent 60%),
    radial-gradient(1000px 500px at 88% 0%, rgba(107, 114, 128, 0.28), transparent 60%),
    linear-gradient(180deg, #F9FAFB 0%, #F3F4F6 40%, #FAFAFA 100%);
}
.bg-team {
  background:
    radial-gradient(1200px 600px at 12% 0%, rgba(59, 130, 246, 0.35), transparent 60%),
    radial-gradient(1000px 500px at 88% 0%, rgba(37, 99, 235, 0.28), transparent 60%),
    linear-gradient(180deg, #EFF6FF 0%, #DBEAFE 40%, #FAFAFA 100%);
}

/* Full-bleed wrapper */
.full-bleed { position: relative; left: 50%; right: 50%; margin-left: -50vw; margin-right: -50vw; width: 100vw; }

/* Organic bands (Home) */
.band-home { position: relative; padding: clamp(24px, 6vw, 56px) 0; }
.band-home::before {
  content: "";
