        url(var(--asset-base, "") "/art/home_light_wave_bottom.svg") bottom / cover no-repeat;
}
.dark .band-home::before { background-image: url(var(--asset-base, "") "/art/home_band_dark.png"); }

/* Organic bands (Owners) */
.band-owners { position: relative; padding: clamp(24px, 6vw, 56px) 0; }
.band-owners::before {
  content: "";
  position: absolute; inset: 0;
  background-image: url(var(--asset-base, "") "/art/owners_band_light.png");
