}
.dark .band-owners::before { background-image: url(var(--asset-base, "") "/art/owners_band_dark.png"); }

/* Wave 2: iPad hamburger enforcement */
@media (max-width: 1024px){
  [data-testid="nav-links"]{ display:none !important; }
  [data-testid="nav-toggle"]{ display:inline-flex !important; }
}
@media (min-width: 1025px){
  [data-testid="nav-links"]{ display:flex !important; }
  [data-testid="nav-toggle"]{ display:none !important; }
}
