/* --- Full-bleed gradient tokens (low-contrast, modern) --- */
.bg-amber {
  background:
    radial-gradient(1200px 600px at 10% 0%, rgba(255, 183, 77, 0.35), transparent 60%),
    radial-gradient(1000px 500px at 90% 0%, rgba(255, 138, 101, 0.28), transparent 60%),
    linear-gradient(180deg, #FFF6E8 0%, #FDF7EF 40%, #FAFAFA 100%);
}
.bg-citrine {
  background:
    radial-gradient(1200px 600px at 12% 0%, rgba(205, 220, 57, 0.35), transparent 60%),
    radial-gradient(1000px 500px at 88% 0%, rgba(124, 179, 66, 0.28), transparent 60%),
    linear-gradient(180deg, #F5FAE8 0%, #F4FAEE 40%, #FAFAFA 100%);
}
.bg-tigereye {
  background:
    radial-gradient(1200px 600px at 12% 0%, rgba(255, 160, 0, 0.18), transparent 60%),
    radial-gradient(1000px 500px at 85% 0%, rgba(121, 85, 72, 0.12), transparent 60%),
    linear-gradient(180deg, #FFF2E0 0%, #FAEEE7 40%, #FAFAFA 100%);
}
.bg-lapis {
  background:
    radial-gradient(1200px 600px at 12% 0%, rgba(33, 150, 243, 0.18), transparent 60%),
    radial-gradient(1000px 500px at 88% 0%, rgba(63, 81, 181, 0.14), transparent 60%),
    linear-gradient(180deg, #EDF5FF 0%, #EEF1FA 40%, #FAFAFA 100%);
}
.bg-obsidian {
  background:
    radial-gradient(1200px 600px at 12% 0%, rgba(84, 110, 122, 0.22), transparent 60%),
    radial-gradient(1000px 500px at 85% 0%, rgba(69, 90, 100, 0.16), transparent 60%),
    linear-gradient(180deg, #F1F4F6 0%, #EFF2F4 40%, #FAFAFA 100%);
}
.bg-teal {
  background:
    radial-gradient(1200px 600px at 12% 0%, rgba(0, 150, 136, 0.16), transparent 60%),
    radial-gradient(1000px 500px at 88% 0%, rgba(77, 182, 172, 0.14), transparent 60%),
    linear-gradient(180deg, #E8F7F5 0%, #ECF8F7 40%, #FAFAFA 100%);
}

/* --- Organic mask utility for backgrounds --- */
/* Soft hand-cut top & bottom with gentle randomness (SVG mask via data URL) */
.mask-organic {
  -webkit-mask-image: url("data:image/svg+xml,%3Csvg width='1200' height='200' viewBox='0 0 1200 200' xmlns='http://www.w3.org/2000/svg'%3E%3Cdefs%3E%3ClinearGradient id='g' x1='0' y1='0' x2='0' y2='1'%3E%3Cstop offset='0' stop-color='%23000'/%3E%3Cstop offset='1' stop-color='%23000'/%3E%3C/linearGradient%3E%3C/defs%3E%3Cpath d='M0,30 C120,60 240,0 360,30 C480,60 600,10 720,35 C840,60 960,5 1080,25 C1140,35 1200,20 1200,20 L1200,180 C1080,150 960,200 840,170 C720,140 600,195 480,165 C360,135 240,190 120,170 C60,160 0,175 0,175 Z' fill='url(%23g)'/%3E%3C/svg%3E");
