  mask-position: top, bottom;
}

/* Wave 9: remove .glass overrides here; .glass lives in globals.css */

/* optional shimmer removed; keep a light noise wash if you want */
.mineral-wash { 
  pointer-events: none;
  background: 
    radial-gradient(circle at 20% 80%, rgba(255,255,255,0.1) 0%, transparent 50%),
    radial-gradient(circle at 80% 20%, rgba(255,255,255,0.05) 0%, transparent 50%);
  animation: mineral-shimmer 8s ease-in-out infinite;
}

@keyframes mineral-shimmer {
  0%, 100% { opacity: 0.3; }
  50% { opacity: 0.6; }
}

/* Additional gradient classes for other pages */
.bg-copper {
  background:
    radial-gradient(1200px 600px at 12% 0%, rgba(222, 138, 90, 0.18), transparent 60%),
